function escapeHTML(e){return e.replace(/&/g,"&lt;").replace(/</g,"&lt;").replace(/>/g,"&gt;").replace(/"/g,"&quot;").replace(/'/g,"&#x27;")}function truncateString(e,n){return e?(e=e.replace(/\r?\n/g,"")).length<=n?e:e.substring(0,n-3)+"...":null}function isAdmin(e){$.cookie("adminSession_"+e)&&$("#navL").html($("#navL").html()+`<li class="nav-item"><a class="nav-link" aria-current="page" href="/admin/${e}">管理パネル</a></li>`)}